import React from 'react'
import { Formik, Field, Form, ErrorMessage } from 'formik'
import * as yup from 'yup'
import { TextField, Button } from '@material-ui/core'
import Autocomplete from '@material-ui/core/Autocomplete'
import cities from '../data/city'

const Studentform = () => (
	<>
		<Formik
			initialValues={{
					firstName: '',
					lastName: '',
			}}
			validationSchema={validationSchema}
			onSubmit={(values, { setSubmitting }) => {
        setTimeout(() => {
          alert(JSON.stringify(values, null, 2));
          setSubmitting(false);
        }, 400);
      }}
    >
      {({ isSubmitting }) => (
        <Form>
					<div className = "student-field">
						<TextField label = "First Name" required variant = "filled" type="name" name="firstName" />
						<ErrorMessage name="firstName" color = "red" component="div" />
					</div>
					<div className = "student-field">
						<TextField label = "Last Name" required variant = "filled" type="name" name="lastName" />
						<ErrorMessage name="lastName" component="div" />
          </div>
					<div className = "student-field">
					<TextField type = "email" required label="Email" variant = "filled" helperText="@drexel.edu only"/>
					</div>
					<div className = "student-field">
					<TextField label="Phone" required type = "number" variant = "filled"/>
					</div>
					<div className = "student-field">
					<TextField label="Location (City)" required type = "" variant = "filled"/>
					</div>
					<Autocomplete freeSolo options={cities.map((option) => {option.city, option.state})} renderInput={(params) => (
						<TextField 
							{...params} label="Location" required margin="normal" variant="outlined" />
							)}
      		/>
					<div>
						<Button variant="contained" color = "Primary" type="submit" disabled={isSubmitting}>
							Submit
						</Button>
					</div>
        </Form>
      )}
    </Formik>
	</>
)

const validationSchema = yup.object(
	{
		firstName: yup
		.string()
	},
	{
		lastName: yup
		.string()
	}
)
export default Studentform
