import React from 'react'
import { Formik, Field, Form} from 'formik'
import * as Yup from 'yup'

const Studentform = () => (
	<>
		<Formik
			initialValues={{
					firstName: '',
					lastName: '',
					}}
			validate={(values) => {
				const errors = {}

				if (values.firstName.includes('bob')) {
					errors.firstName = 'No Bob!'
				}

				return errors;
			}}
			onSubmit={(data, {setSubmitting, resetForm }) => {
				setSubmitting(true)
				console.log(data)
				setSubmitting(false)
			}}
			>
				{({ values, isSubmitting}) => (
					<Form>
						<div>
							<Field placeholder = "Sharon" name='firstName' type = 'input' />
						</div>
						<div>
							<Field placeholder = "Smith" name='lastName' type = 'input' />
						</div>
						<div>
						<button disabled={isSubmitting} type="submit">
							Submit!
						</button>
						</div>
						<pre>{JSON.stringify(values, null, 2)}</pre>
					</Form>
				)}

			</Formik>
	</>
)

export default Studentform
