import React from 'react'
import { Formik, Field, Form, ErrorMessage } from 'formik'
import * as yup from 'yup'
import { TextField, Button, FormLabel } from '@material-ui/core'
import Autocomplete from '@material-ui/lab/Autocomplete'
import cities from '../data/city'
import MuiPhoneInput from 'material-ui-phone-number'

const Studentform = () => (
	<>
		<Formik
			initialValues={{
					firstName: '',
					lastName: '',
			}}
			validationSchema={validationSchema}
			onSubmit={(values, { setSubmitting }) => {
        setTimeout(() => {
          alert(JSON.stringify(values, null, 2));
          setSubmitting(false);
        }, 400);
      }}
    >
      {({ isSubmitting }) => (
        <Form>
					<div className = "student-fields">
						<div className = "student-field">
							<FormLabel>First Name</FormLabel>
							<TextField label = "First Name" margin = "normal" required variant = "outlined" type="name" name="firstName" />
							<ErrorMessage name="firstName" color = "red" component="div" />
						</div>
						<div className = "student-field">
							<TextField label = "Last Name" margin = "normal" required variant = "outlined" type="name" name="lastName" />
							<ErrorMessage name="lastName" component="div" />
						</div>
						<div className = "student-field">
						<TextField type = "email" margin = "normal" required label="Email" variant = "outlined" helperText="@drexel.edu only"/>
						</div>
						<div>
						<MuiPhoneInput defaultCountry={'us'} onlyCountries={['us']} required margin = "normal" />
						</div>
						<div>
					</div>
					<div className = "auto-fields" >
						<div>
						<Autocomplete freeSolo options={cities.map((option) => option.city)} renderInput={(params) => (
							<TextField 
								{...params} label="City" margin = "normal" required variant = "outlined" />
								)}
						/>
						</div>
						<div>
						<Autocomplete freeSolo options={cities.map((option) => option.state)} renderInput={(params) => (
							<TextField 
								{...params} label="State" margin = "normal" required variant = "outlined" />
								)}
						/>
						</div>
					</div>
						<div>
							<Button variant="contained" color = "primary" type="submit" disabled={isSubmitting}>
								Submit
							</Button>
						</div>
					</div>
				</Form>
      )}
    </Formik>
	</>
)

const validationSchema = yup.object(
	{
		firstName: yup
		.string()
		.required()
	},
	{
		lastName: yup
		.string()
		.required()
	}
)
export default Studentform
